local Players = game:GetService("Players")\nlocal ReplicatedStorage = game:GetService("ReplicatedStorage")\n\n-- Create the Billboard GUI\nlocal function createBillboard()\n    local billboard = Instance.new("BillboardGui")\n    billboard.Size = UDim2.new(0, 200, 0, 100)\n    billboard.Adornee = game.Workspace.Luna -- Replace with your dog's model name\n    billboard.AlwaysOnTop = true\n    billboard.MaxDistance = 50\n\n    local textLabel = Instance.new("TextLabel", billboard)\n    textLabel.Size = UDim2.new(1, 0, 1, 0)\n    textLabel.Text = "I'm Working Please Don't Pet Me"\n    textLabel.TextColor3 = Color3.fromRGB(255, 0, 0)\n    textLabel.BackgroundTransparency = 1\n\n    return billboard\nend\n\n-- Function to display/hide the billboard based on working mode\nlocal function updateBillboard(isWorking)\n    local billboard = createBillboard()\n    if isWorking then\n        billboard.Parent = game.Workspace.Luna\n    else\n        billboard:Destroy()\n    end\nend\n\n-- Example usage: toggle the billboard\nwhile true do\n    local isWorking = true -- Replace with your logic to determine working mode\n    updateBillboard(isWorking)\n    wait(5) -- Update every 5 seconds\nend